import QuestionCard from './QuestionCard.vue';
export default {
  name: 'PanelForum',
  components: {
    QuestionCard: QuestionCard
  },
  props: {
    forumName: String,
    questions: Array,
    loggedUserId: String
  },
  data: function data() {
    return {
      questionsData: []
    };
  },
  watch: {
    questions: {
      handler: function handler(questions) {
        var _this = this;

        this.questionsData = questions.map(function (question) {
          var key = question.key,
              userId = question.userId,
              username = question.username,
              content = question.content,
              time = question.time,
              usersHandsUp = question.usersHandsUp;
          var isHandsUp = usersHandsUp.findIndex(function (user) {
            return user.userId == _this.loggedUserId;
          }) >= 0;
          var isMine = userId == _this.loggedUserId;
          return {
            key: key,
            username: username,
            content: content,
            time: time,
            isHandsUp: isHandsUp,
            isMine: isMine
          };
        });
      },
      deep: true
    }
  }
};
