import ButtonHandsUp from './ButtonHandsUp.vue';
export default {
  name: 'QuestionCard',
  components: {
    ButtonHandsUp: ButtonHandsUp
  },
  props: {
    question: Array
  },
  data: function data() {
    var hours = new Date(this.question.time).getHours().toString(),
        minutes = new Date(this.question.time).getMinutes().toString();
    if (hours.length < 2) hours = "0" + hours;
    if (minutes.length < 2) minutes = "0" + minutes;
    return {
      time: hours + "." + minutes
    };
  }
};
