const getUrlParams = () => new URLSearchParams(window.location.search);

export default {
	get: key => getUrlParams().get(key),  // nullable
	set: (key, val) => {
		const urlParams = getUrlParams();
		urlParams.set(key, val);
		window.location.search = urlParams;
	},
	delete: key => {
		const urlParams = getUrlParams();
		urlParams.delete(key);
		window.location.search = urlParams;
	}
};
